mapper.insert:
INSERT INTO board
(id, title, writer, content, regDate, viewCnt, price, img, orgImg)
VALUES(#{id}, #{title}, #{writer}, #{content}, #{regDate}, #{viewCnt}, #{price}, #{img}, #{orgImg});

mapper.update:
UPDATE board
SET title = #{title}, content = #{content}
WHERE id = #{id};

UPDATE board
SET viewCnt = viewCnt+1
WHERE id = #{id};
